var modal=document.querySelector(".open-modal"),modalContent=document.querySelector(".popup"),modalClose=document.querySelector(".close-modal"),form=document.querySelector(".popup form"),login=document.querySelector("[name=name]"),email=document.querySelector("[name=email]"),letter=document.querySelector("[name=letter]");modal.addEventListener("click",function(a){a.preventDefault(),modalContent.classList.add("modal-content-show")}),modalClose.addEventListener("click",function(a){a.preventDefault(),modalContent.classList.remove("modal-content-show")}),form.addEventListener("submit",function(a){login.value&&email.value&&letter.value||(login.value?login.classList.remove("modal-error"):(a.preventDefault(),login.classList.add("modal-error")),email.value?email.classList.remove("modal-error"):(a.preventDefault(),email.classList.add("modal-error")),letter.value?letter.classList.remove("modal-error"):(a.preventDefault(),letter.classList.add("modal-error")))});